package com.razvan.stritiromania.Managers;import android.content.Context;import android.util.Log;/** * Created by Razvan'S PC on 19.03.2016. */public class Manager {    private final static String TAG = "Main Manager";    private static Manager instance;    private RssFeedManager rssFeedManager;    private DBManager dbManager;    private Manager(Context context){        rssFeedManager=new RssFeedManager();        dbManager=new DBManager(context);        Log.v(TAG, "Done initializing managers");    }    public static Manager getInstance(Context context) {        if (instance == null) {            instance = new Manager(context);        }        return instance;    }    public RssFeedManager getRssFeedManager() {        return rssFeedManager;    }    public DBManager getDbManager() {        return dbManager;    }}