package com.razvan.stritiromania.Managers;import android.util.Log;/** * Created by Razvan'S PC on 19.03.2016. */public class Manager {    private final static String TAG = "Main Manager";    private static Manager instance;    private RssFeedManager rssFeedManager;    private Manager(){        rssFeedManager=new RssFeedManager();        Log.v(TAG, "Done initializing managers");    }    public static Manager getInstance() {        if (instance == null) {            instance = new Manager();        }        return instance;    }    public RssFeedManager getRssFeedManager() {        return rssFeedManager;    }}