package com.razvan.stritiromania.Managers;import com.einmalfel.earl.EarlParser;import com.einmalfel.earl.Feed;import com.einmalfel.earl.Item;import org.xmlpull.v1.XmlPullParserException;import java.io.IOException;import java.io.InputStream;import java.net.URL;import java.util.ArrayList;import java.util.List;import java.util.zip.DataFormatException;/** * Created by Razvan'S PC on 19.03.2016. */public class RssFeedManager  {    private final static String TAG = "RSS_FEED_Manager";    public List<Item> readFeed(final String URL) throws XmlPullParserException, IOException, DataFormatException {         List<Item> itemList= new ArrayList<>();                InputStream inputStream =  new URL(URL).openConnection().getInputStream();                Feed feed = EarlParser.parseOrThrow(inputStream, 0);                for (Item item : feed.getItems()) {                    itemList.add(item);                }        return itemList;    }    }