package com.razvan.stritiromania.Pojos.Models;import android.util.Log;import com.einmalfel.earl.Item;import com.razvan.stritiromania.Pojos.Interfaces.SimpleObservable;import java.util.List;/** * Created by Razvan'S PC on 19.03.2016. */public class NewsFeedModel extends SimpleObservable<NewsFeedModel> {    private String TAG ="NewsFeedModel";    private List<Item> itemsList;    public NewsFeedModel() {}    public List<Item> getItemsList() {        return itemsList;    }    public void setItemsList(List<Item> itemsList) {        Log.e(TAG, "setItemsList: "+itemsList.size() );        this.itemsList = itemsList;        notifyChanged();    }    public void notifyChanged(){        notifyObservers();    }}